function Hotel(t,e,o){this.name=t,this.rooms=e,this.booked=o,this.checkAvailability=function(){return this.rooms-this.booked}}var stubHotel=new Hotel("Stub",30,25),okiHotel=new Hotel("Oki",35,20),details1=stubHotel.name+" rooms: ";details1+=stubHotel.checkAvailability();var elHotel=document.getElementById("hotel1");elHotel.textContent=details1;var details2=okiHotel.name+" rooms: ";details2+=okiHotel.checkAvailability();var elHotel2=document.getElementById("hotel2");elHotel2.textContent=details2;